import React from 'react';


export default function Even(props) {    
    if (props.state % 2 === 0) {
        return (
        <div>
        <p>{props.state}</p>
        <p>{props.title}</p>
        </div>
        )
    } else {
        return  (
            <div>
            {/* <p>{props.state}</p> */}
            <p>{props.title}</p>
            </div>
            )

    }
}
