import React from 'react';
import Odd from './js/odd'
import Even from './js/even'

// import ReactDOM from 'react-dom';

class Clock extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: '',
      counter: 0,
    };
    console.log('constructor');
  }

  componentDidMount() {
    console.log('componentDidMount');

    setInterval(() => {
      this.setState({
        counter: this.state.counter + 1,
      });
    }, 500);
  }

  render() {
    console.log('render');

    return (
      <React.Fragment>
        <label> нечетные числа: </label>
        <Odd state={this.state.counter} title={this.state.title} />
        <input></input>
        <label> четные числа:</label>
        <input><Even state={this.state.counter} title={this.state.title} /></input>
      </React.Fragment>
    );
  }
}

export default Clock;
