import React from 'react';

const title = 'Hello';
const el = <h1>{ title }</h1>;

class App2 extends React.Component {
 constructor(props) {
   super(props);
 }

 getName() {
   return 'Vitalik';
 }

 render() {
   return (
     <h1>
       Class Component
       { this.getName() }
       { this.props.name }
       { this.props.lastname }
       { this.props.title }
     </h1>
   );
 }
}

const App = ({ name, lastname, title }) => {
  return (
    <div>
      Hello React
      { el }
      { name }
      { lastname }
      { title }
      <App2 name="Valera" lastname="Vvv" title={title} />
    </div>
  );
};

export default App;
