import React from 'react';
// import ReactDOM from 'react-dom';

class Clock extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: '',
      counter: 0,
    };
    console.log('constructor');
  }

  componentDidMount() {
    console.log('componentDidMount');

    setInterval(() => {
      this.setState({
        counter: this.state.counter + 1,
        title: this.state.counter === 10 ? 'Wiiin!' : ''
      });
    }, 500);
  }

  // foo() {
  //   if (this.state.counter === 10) {
  //     this.setState({
  //       title: 'Wiiiin!'
  //     });
  //   }
  // }

  componentDidUpdate(prevProps, prevState, snapshot) {
    if (this.state.counter === 10) {
      this.setState({
        ...this.state,
        title: 'Wiiiin!',
      });
    }
  }

  render() {
    console.log('render');

    return (
      <React.Fragment>
        <h1>
          { this.state.counter }
        </h1>
        <h2>
          { this.state.title }
        </h2>
      </React.Fragment>
    );
  }
}

export default Clock;
